import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import store from "./state";




export let reRender = (state)=>{
    ReactDOM.render(
        <React.StrictMode>
            <App onChangePostText={store.onChangePostText} onChangeMessage={store.onChangeMessage} state={state} sendMessage={store.sendMessage} addPost={store.addPost} />
        </React.StrictMode>,
        document.getElementById('root')
    );
}
reRender(state)
subscribe(reRender)
