// import { reRender } from "./index.js"
let store={
     _state: {
        profilePage: {
            postData: [
                {message:"Напомним, Death Stranding Director’s", id:1, likes:5},
                {message:"«Меня зовут Пророк» — релизный трейлер Crysis Remastered Trilogy", id:2, likes:8},
                {message:"Руководитель Deathloop раскрывает секреты игрового дизайна", id:3, likes:22},
            ],

            newPostText: "Какие у вас Новости?"
        },

        dialogPage:{
            dialogData:[
                {name: "Владимир", id: "1"},
                {name: "Мария", id: "2"},
                {name: "Михаил", id: "3"},
                {name: "Вячеслав", id: "4"},
                {name: "Алексей", id: "5"},
            ],

            messageData:[
                {message: "Привет. Как дела?", id: "1"},
                {message: "Здравствуйте.", id: "2"},
                {message: "Здарова. Пойдешь гулять?", id: "3"},
                {message: "Привет. Ты вечером свободен?", id: "4"},
            ],

            newMessageText:"Всем привет!"
        },

        userPage:{
            friends: [
                {name:"Владимир", id:1},
                {name:"Мария", id:2},
                {name:"Михаил", id:3},
                {name:"Вячеслав", id:4},
                {name:"Алексей", id:5},
            ]
        },


    },

    reRender(){

    },

    subscribe (reRenderFromIndex) {
        this.reRender=reRenderFromIndex
    },

    onChangePostText  (text) {
        this.state.profilePage.newPostText=text
        this.reRender (this.state)
    },

    onChangeMessage (text) {
        this.state.dialogPage.newMessageText=text
        this.reRender(this.state)
    },

    addPost  (postText) {
        let newPost = {
            message: postText,
            id: 4,
            likes: 0,
        }

        this.state.profilePage.postData.unshift(newPost)
        this.reRender(this.state)
    },

    sendMessage  (messageText) {
        let newMessage = {
            message: messageText,
            id: 5,
        }

        this.state.dialogPage.messageData.push(newMessage)
        this.reRender(this.state)
    },
}
